mapper.select:
select

from point_record
where
activity_id=#{activityId,jdbcType=BIGINT}
and user_id=#{userId,jdbcType=BIGINT}
and cause=#{cause,jdbcType=TINYINT}

mapper.update:
update point_record
set
status = #{status,jdbcType=TINYINT},
update_date = #{updateDate,jdbcType=TIMESTAMP}
where
activity_id=#{activityId,jdbcType=BIGINT}
and user_id=#{userId,jdbcType=BIGINT}
and cause=#{cause,jdbcType=TINYINT}
